let amper = prompt("Введите кол-во дней")
let year = 365;
let hour = 24;
let min = 60;
let week = 7;
let month = 31;
let sec = 60;
let den =(amper/year)+" кол -во годов " +"\n" +(amper/month) +" кол -во месяцев "+"\n" + (amper/week) +" кол-во недель " +"\n"+ (amper*hour) + " кол-во часов "+"\n"+ (amper*hour*min)+" кол -во минут "+"\n" +(amper*hour*min*sec)+ " кол-во секунд";

console.log(den)
if(amper<year){
    console.log("меньше года")
}
if (amper<month){
    console.log("меньше месяца")

}
if(amper<week){
    console.log("меньше недели")
}
